import { HtmlHTMLAttributes, FC } from 'react';

interface NextImageTurntableProps {
    /** List of image `src` attributes. */
    images: string[];
    /** Index of image to show first. */
    initialImageIndex?: number;
    /** The amount a "drag" has to move before an image changes to next or previous. */
    movementSensitivity?: number;
}
/** Base props *and* all available HTML element props. */
type NextImageTurntableFullProps = HtmlHTMLAttributes<HTMLDivElement> & NextImageTurntableProps;

/** Base `className` for images. */
declare const CLASS_NAME_IMG = "__react-image-turntable-img";
/** `className` of first rendered image (sets the size of the main component). */
declare const CLASS_NAME_IMG_PRIMARY: string;
/** `className` of subsequent images. */
declare const CLASS_NAME_IMG_SECONDARY: string;
declare const NextImageTurntable: FC<NextImageTurntableFullProps>;

export { CLASS_NAME_IMG, CLASS_NAME_IMG_PRIMARY, CLASS_NAME_IMG_SECONDARY, NextImageTurntable, NextImageTurntableFullProps, NextImageTurntableProps };
